// "rfc"  is used as shortcut
import React from 'react'
import PropTypes from 'prop-types'
import { BrowserRouter as Router, Route, Routes ,Link } from 'react-router-dom';

export default function Navbar(props) {
  return (
    <nav className={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>
    <div className="container-fluid">
      <a className="navbar-brand" href="/">{props.title}</a>
      <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
          <li className="nav-item">
            <Link className="nav-link active" aria-current="page" to="/">Home</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" href="/">{props.aboutText}</Link>
          </li>
        
        </ul>
       {/* // <form className="d-flex">
        //   <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        //   <button className="btn btn-outline-success" type="submit">Search</button>
       // </form>*/}

    {/*  <div className={`form-check form-switch text-${props.mode==='light'?'#0f7d25':'light'}`}>
       <input className="form-check-input" onClick={props.toggleMode1} type="checkbox" role="switch" id="flexSwitchCheckDefault"/>
       <label className="form-check-label mx-2" htmlFor="flexSwitchCheckDefault">Enable green  mode</label>
      </div> */}



        <div className={`form-check form-switch text-${props.mode==='light'?'dark':'light'}`}>
  <input className="form-check-input" onClick={props.toggleMode} type="checkbox" role="switch" id="flexSwitchCheckDefault"/>
  <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Enable dark mode</label>
</div>
      </div>
    </div>
  </nav>
  );
}

Navbar.propTypes ={
    title:PropTypes.string.isRequired,
    aboutText:PropTypes.string.isRequired
}

//if props is not passed then these default value will appear 
Navbar.defaultProps ={
  title :' set title here',
  aboutText : 'About '
}
