import React , {useState} from 'react'


export default function Form(props) {

  const handleUpClick =()=>{
   // console.log("uppercase was clicked" + text);
    let newtext = text.toUpperCase();
    setText(newtext);
  }
  const handleloClick =()=>{
    // console.log("uppercase was clicked" + text);
     let newtext = text.toLowerCase();
     setText(newtext);
   }

  const handleOnChange=(event)=>{
   // console.log("on change");
    setText(event.target.value);
  }

  const handleCleartext=()=>{
    // console.log("on change");
    let newtext = ('');
    setText(newtext);
   }

   const convertfont=()=>{
    // console.log("on change");
    <font><i class="fa fa-italic" aria-hidden="true"></i></font>
    let newtext = text
    setText(newtext);
   }

  
  //const[count ,setCount] = useState(0);  --->sytnax of usestate hooks 

  const[text ,setText] = useState('');

 //text = "new text";  //wrong way to change the state
 //setText ="new text";  //correct way to change the state
  return (
    <>

    <div className="Container">
<h1>{props.heading}</h1>
  <div className="mb-3">
  {/* <label for ="=myBox" class="form-lable">Example textarea </label>*/}
   <textarea className="form-control"value={text} onChange={handleOnChange} id ="myBox" rows="7"></textarea>

    
    </div>

  
<button className="btn btn-primary mx-2" onClick={handleUpClick}>convert to uppercase</button>
<button className="btn btn-primary mx-2" onClick={handleloClick}>convert to lowercase</button>
<button className="btn btn-primary mx-2" onClick={handleCleartext}>ClearText</button>
<button className="btn btn-primary mx-2" onClick={convertfont}>To italic</button>


    </div>
    <div className="Container my-3">
    <h1>Your text summary</h1>
    <p>{text.split(" ").length} words and {text.length} characters</p>
    <p>{0.008* text.split(" ").length} minute read</p>
    <h2>preview</h2>
    <p>{text}</p>
    </div>

    </>

  )
}
